'use strict';

module.exports = require('./lib/express');


var express = require('express');
var app = express();
app.use(express.static(__dirname+'/public'));
app.set('view engine', 'jade');
app.get('/',function(req,res){
	res.render('index.jade');
});

app.get('/cruz',function(req,res){
	res.render('./cruz');
});
app.get('/democrat',function(req,res){
	res.render('./democ');
});
app.get('/gop',function(req,res){
	res.render('./repub');
});
app.get('/sanders',function(req,res){
	res.render('./sanders');
});
app.get('/clinton',function(req,res){
	res.render('./clinton');
});
app.get('/trump',function(req,res){
	res.render('./trump');
});
app.get('/rubio',function(req,res){
	res.render('./rubio');
});
app.get('/map',function(req,res){
	res.render('map.jade');
});

app.get('/alexTest', function(req, res){
	res.render('indexAkyei.jade');
});
app.listen(80, function () {
	console.log('Example app listening on port 80');
});

